import { ref } from 'vue';
import postData from '../assets/data.json';

export default function getPost(id) {
  const post = ref(null);

  function fetchPost() {
    post.value = postData.find(p => p.id === parseInt(id)) || null;
  }

  return { post, fetchPost };
}
