import {ref} from 'vue';
import postData from '../assets/data.json';

export default function getPosts(){
    const posts = ref([]);
    function fetchPosts(){
        // data.json contains an object with a `posts` array
        posts.value = postData.posts || postData;

    }
    return {posts ,fetchPosts};
}
