import { ref } from 'vue';
import usePostsStore from '../composables/postStore';
export default {
  setup() {
    const title = ref('');
    const body = ref('');
    const tags = ref('');
    const {
      addPost
    } = usePostsStore();
    function handleSubmit() {
      addPost({
        title: title.value,
        body: body.value,
        tags: tags.value
      });
      alert("Post ajouté !");
      title.value = '';
      body.value = '';
      tags.value = '';
    }
    return {
      title,
      body,
      tags,
      handleSubmit
    };
  }
};
