import "core-js/modules/es.array.push.js";
import { ref } from 'vue';
import { useRouter } from 'vue-router';
import { usePostsStore } from '../composables/postsStore';
export default {
  name: 'CreatePostView',
  setup() {
    const router = useRouter();
    const store = usePostsStore();
    const title = ref('');
    const body = ref('');
    const tags = ref('');
    function handleSubmit() {
      if (!title.value.trim() || !body.value.trim()) {
        alert('Titre et contenu sont obligatoires');
        return;
      }

      // Ajouter le post au store
      store.addPost({
        title: title.value,
        body: body.value,
        tags: tags.value.split(',')
      });

      // Réinitialiser le formulaire
      title.value = '';
      body.value = '';
      tags.value = '';
      alert('Post ajouté avec succès!');

      // Rediriger vers l'accueil
      router.push('/');
    }
    return {
      title,
      body,
      tags,
      handleSubmit
    };
  }
};
