import {ref} from 'vue';
import postData from '../assets/data.json';

export default function getPost(id){
    const post = ref(null);
    function fetchPost(){
        const foundPost = postData.find(p => p.id === parseInt(id));
    post.value = foundPost || null;

    }
    return {post ,fetchPost};
}
