import { ref } from 'vue';
import postData from '../assets/data.json';

export default function getPost(id) {
  const post = ref({});

  function fetchPost() {
    const posts = postData.posts || [];
    post.value = posts.find(p => p.id === parseInt(id)) || { title: '', body: '', tags: [] };
  }

  return { post, fetchPost };
}
